import {assign, createMachine} from 'xstate'

export const todosMachineFactory = () =>
  /** @xstate-layout -6E9nkPryxiAWXkruUXXgVdWeWhLsgt824zG0CNIcjJCsw2vXkVgcWE7QcB0nSPd9jlsfkvS0REYjQ5tNWyIA */
  createMachine(
    {
      context: {
        todos: [] as string[],
        error: undefined as string | undefined,
        todo: '',
      },
      tsTypes:
        {} as import('./todos-machine.typegen').Typegen0,
      schema: {
        services: {} as {
          fetch: {
            data: string[]
          }
        },
        events: {} as
          | {
              type: 'onEdit'
            }
          | {
              type: 'onUpdate'
              value: string
            },
      },
      predictableActionArguments: true,
      id: 'todos-machine',
      initial: 'indicateFetch',
      states: {
        indicateFetch: {
          invoke: {
            src: 'fetch',
            onDone: [
              {
                actions: 'setTodosInContext',
                target: 'read',
              },
            ],
            onError: [
              {
                actions: 'setErrorInContext',
                target: 'error',
              },
            ],
          },
        },
        read: {
          on: {
            onEdit: {
              target: 'edit',
            },
          },
        },
        error: {},
        edit: {
          initial: 'update',
          states: {
            update: {
              on: {
                onUpdate: {
                  actions: 'setUpdateInContext',
                },
              },
            },
          },
        },
      },
    },
    {
      actions: {
        setTodosInContext: assign((_, event) => {
          return {
            todos: event.data,
          }
        }),
        setErrorInContext: assign((_, event) => {
          return {
            error: (event.data as Error).message,
          }
        }),
        setUpdateInContext: assign((_, event) => {
          return {
            todo: event.value,
          }
        }),
      },
    },
  )

export async function fetch() {
  return Array.from([
    'todo1',
    'todo2',
    'todo3',
    'todo4',
    'todo5',
    'todo6',
  ])
}
