import {createMachine, assign} from 'xstate'

export const todosMachineFactory = () =>
  /** @xstate-layout -MlPO4jj5cbosi4QoViuhSpIKjJ-sC5qCLOC5qtHFjwrpQjkHLpieyAk40WtQtDgkc-D4Qi5PCFCXlSSBvpSho1mg1aSYzAyrEz7H4QgFKJ5uTdvA4Mji-Py4ohoYdQmK+Xr9hk5QrBhQQRrltq1rsHDC4T4EftuT4BVg9k5sXrAp4UsFdk4XvkgA */
  createMachine(
    {
      context: {
        todos: [] as string[],
        error: undefined as string | undefined,
      },
      tsTypes:
        {} as import('./todos-machine.typegen').Typegen0,
      schema: {
        services: {} as {
          fetch: {
            data: string[]
          }
        },
      },
      predictableActionArguments: true,
      id: 'todos-machine',
      initial: 'fetchIndicated',
      states: {
        fetchIndicated: {
          invoke: {
            src: 'fetch',
            onDone: [
              {
                actions: 'setTodosInContext',
                target: 'fetched',
              },
            ],
            onError: [
              {
                actions: 'setErrorInContext',
                target: 'errored',
              },
            ],
          },
        },
        fetched: {},
        errored: {},
      },
    },
    {
      actions: {
        setTodosInContext: assign((_, event) => {
          return {
            todos: event.data,
          }
        }),
      },
    },
  )
