import {assign, createMachine} from 'xstate'

export const todosMachineFactory = () =>
  /** @xstate-layout -lC5xiNmR7geIB6xQk3F+HHUABEwM0wIx-nNAcDljZkj5KIEgs5sjjdrDwlFYpsYusfO5fGCYnZ3KkbASiS9+gRfoxKABXAwQWSfMgAVQ1fyQIFm8zMhqWtjsmModhOzjSPmSMVu--5TLOhgMaaIGxGxbJuHssFsXmFy8YWlA+FyqQJKuWi+PiEnYeR0mUWAWDyTU2rDBAQGMUZzIwqcvgpNBcIeGxvGOGyDruOc9mBt6ooXqGGneb5dQNOwFiwMgbZ4G0jA1AAFGCewAJTsOp- */
  createMachine(
    {
      context: {
        todos: [] as string[],
        error: undefined as string | undefined,
        todo: '',
      },
      tsTypes:
        {} as import('./todos-machine.typegen').Typegen0,
      schema: {
        services: {} as {
          read: {
            data: string[]
          }
          save: {
            data: void
          }
          delete: {
            data: void
          }
        },
        events: {} as
          | {
              type: 'onCreate'
            }
          | {
              type: 'onSave'
            }
          | {
              type: 'onUpdate'
              value: string
            }
          | {
              type: 'onDelete'
              todo: string
            }
          | {
              type: 'onRead'
            },
      },
      predictableActionArguments: true,
      id: 'todos-machine',
      initial: 'indicateRead',
      states: {
        indicateRead: {
          invoke: {
            src: 'read',
            onDone: [
              {
                actions: 'setTodosInContext',
                target: 'read',
              },
            ],
            onError: [
              {
                actions: 'setErrorInContext',
                target: 'error',
              },
            ],
          },
        },
        read: {
          on: {
            onCreate: {
              target: 'create',
            },
            onDelete: {
              target: 'delete',
            },
          },
        },
        error: {},
        create: {
          initial: 'update',
          states: {
            update: {
              on: {
                onUpdate: {
                  actions: 'setUpdateInContext',
                },
                onSave: {
                  target: 'save',
                },
              },
            },
            save: {
              invoke: {
                src: 'save',
                onDone: [
                  {
                    target:
                      '#todos-machine.indicateRead',
                  },
                ],
                onError: [
                  {
                    actions: 'setErrorInContext',
                    target: 'update',
                  },
                ],
              },
            },
          },
        },
        delete: {
          invoke: {
            src: 'delete',
            onDone: [
              {
                target: 'indicateRead',
              },
            ],
            onError: [
              {
                actions: 'setErrorInContext',
                target: 'delete-error',
              },
            ],
          },
        },
        'delete-error': {
          after: {
            '3000': {
              target: 'indicateRead',
            },
          },
          on: {
            onRead: {
              target: 'indicateRead',
            },
          },
        },
      },
    },
    {
      actions: {
        setTodosInContext: assign((_, event) => {
          return {
            todos: event.data,
          }
        }),
        setErrorInContext: assign((_, event) => {
          return {
            error: (event.data as Error).message,
          }
        }),
        setUpdateInContext: assign((_, event) => {
          return {
            todo: event.value,
          }
        }),
      },
    },
  )
